import { useEffect, useState } from 'react';
import Login from './components/Login.js';
import './components/Search.js';
import SearchBar from './components/SearchBar.js';
import { SharedDataProvider } from './components/SharedDataContext';
import { getTokenFromUrl } from './components/spotify.js';
import './css/App.css';
import './css/SearchBar.css';


function App() {
   const [token, setToken] = useState();

  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = "";
    const _token = hash.access_token;

    if (_token) {
      setToken(_token);
    }

    console.log("token", token);
  }, [token]);
  return (
    <div>
      <header>
      <p id="title">Bookish Melodies</p>
      <nav>
        <ul>
          <li><a href="./components/Search.js">Search</a></li>
          <li><a href="./TopPlaylists.js">Top Playlists</a></li>
        </ul>
        </nav>
      </header>

      <main>
        <div className="title">
          <h1>Book-Inspired Playlist Generator</h1>
        </div>
        <div className="login">
          <Login />
          <getTokenFromUrl />

        </div>
     <SharedDataProvider>
      <SearchBar />
    </SharedDataProvider>
      </main>
      <footer>
        <p>&copy; 2023 Book-Inspired Playlist Generator</p>
      </footer>
    </div>
  );
}

export default App;
