import React, { useState } from 'react';
import { Button, Form, FormControl, FormGroup, FormLabel } from 'react-bootstrap';

function JokeFetcher() {
  const [joke, setJoke] = useState('');
  const [category, setCategory] = useState('Any');

  const fetchJoke = () => {
    const url = `https://v2.jokeapi.dev/joke/${category}?safe-mode&type=single`;
    fetch(url)
      .then(response => response.json())
      .then(data => setJoke(data.joke))
      .catch(error => console.error('Error fetching joke:', error));
  };

  return (
    <div>
      <Form>
        <FormGroup>
          <FormLabel>Select Joke Category</FormLabel>
          <FormControl as="select" value={category} onChange={e => setCategory(e.target.value)}>
            <option>Any</option>
            <option>Misc</option>
            <option>Programming</option>
          </FormControl>
        </FormGroup>
        <Button onClick={fetchJoke}>Get Joke</Button>
      </Form>

      <textarea value={joke} readOnly className="mt-3 form-control" />
    </div>
  );
}

export default JokeFetcher;
