// BookSearch.js
import { useEffect, useState } from 'react';

function BookSearcher({ bookTitle }) {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    if (bookTitle) {
      const fetchBooks = () => {
        const url = `https://openlibrary.org/search.json?q=${encodeURIComponent(bookTitle)}`;
        fetch(url)
          .then(response => response.json())
          .then(data => {
            setBooks(data.docs); // Setting books to the array of books returned by the API
          })
          .catch(error => console.error('Error fetching books', error));
      };

      fetchBooks();
    }
  }, [bookTitle]); // This effect runs when bookTitle changes

  return (
    <div>
      {books.map((book, index) => (
        <>
          <p key={index}>{book.title}</p>
          <p key={index}>{book.subject}</p>
        </>
      ))}
    </div>
  );
}

export default BookSearcher;
