import { useEffect, useState } from 'react';

function SpotifySearch() {
  const [data, setData] = useState(null);
  const [accessToken, setAccessToken] = useState(null);

  useEffect(() => {
    const handleAuthRedirect = async () => {
      const queryParams = new URLSearchParams(window.location.search);
      const code = queryParams.get('code');
      if (code) {
        await exchangeCodeForToken(code);
      }
    };

    handleAuthRedirect();
  }, []);

  const generateRandomString = (length) => {
    const possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    let text = '';
    for (let i = 0; i < length; i++) {
      text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
  };

  const sha256 = async (plain) => {
    const encoder = new TextEncoder();
    const data = encoder.encode(plain);
    return window.crypto.subtle.digest('SHA-256', data);
  };

  const base64encode = (input) => {
    return btoa(String.fromCharCode(...new Uint8Array(input)))
      .replace(/=/g, '')
      .replace(/\+/g, '-')
      .replace(/\//g, '_');
  };

  const initiateAuthFlow = async () => {
    const codeVerifier = generateRandomString(128);
    localStorage.setItem('code_verifier', codeVerifier);

    const hashed = await sha256(codeVerifier);
    const codeChallenge = base64encode(hashed);

    const clientId = '01c9f82ca3e348e2adc1d98eded52db1';
    const redirectUri = 'http://localhost:3001/callback';
    const scope = 'user-read-private user-read-email';

    const authUrl = new URL('https://accounts.spotify.com/authorize');
    const params = {
      response_type: 'code',
      client_id: clientId,
      scope,
      redirect_uri: redirectUri,
      code_challenge_method: 'S256',
      code_challenge: codeChallenge,
    };

    authUrl.search = new URLSearchParams(params).toString();
    window.location.href = authUrl.toString();
  };

  const exchangeCodeForToken = async (code) => {
    const codeVerifier = localStorage.getItem('code_verifier');
    try {
      const response = await fetch('/api/token', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ code, codeVerifier }),
      });

      const data = await response.json();
      localStorage.setItem('access_token', data.access_token);
      setAccessToken(data.access_token);
      fetchData(data.access_token);
    } catch (error) {
      console.error('Error exchanging code for token:', error);
    }
  };

  const fetchData = async (token) => {
    const searchTerm = 'example'; // Replace with actual search term
    try {
      const response = await fetch(`/api/spotify-search?q=${encodeURIComponent(searchTerm)}&type=track`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const jsonData = await response.json();
      setData(jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
      setData(null);
    }
  };

  return (
    <div>
      <button onClick={initiateAuthFlow}>Login with Spotify</button>
      {accessToken && <button onClick={() => fetchData(accessToken)}>Fetch Data</button>}
      {data && <pre>{JSON.stringify(data, null, 2)}</pre>}
    </div>
  );
}

export default SpotifySearch;
