/* eslint-disable react-hooks/exhaustive-deps */

// import modules
import { useEffect, useState } from 'react';
import {
Route,
BrowserRouter as Router,
Routes,
} from "react-router-dom";
import { SharedDataProvider } from './components/SharedDataContext';

// import css
import './css/App.css';
import './css/SearchBar.css';
import './css/Spotify.css';

// import components
import Login from "./components/Login";
import Navigation from "./components/Navigation/Navigation";
import SearchBar from "./components/SearchBar";
import TopPlaylists from './components/TopPlaylists';

// prep spotify api
import SpotifyWebApi from "spotify-web-api-js";
import { getTokenFromUrl } from "./components/spotify";
const spotify = new SpotifyWebApi();

// prep book api 
import BookSearcher from "./components/BookSearcher";

function App() {
  // spotify authentication
    const [token, setToken] = useState();
  useEffect(() => {
    const hash = getTokenFromUrl();
    window.location.hash = "";
    const _token = hash.access_token;

    if (_token) {
      setToken(_token);
      spotify.setAccessToken(_token);
    }
    console.log("token", token);

  }, []);
  // return html page
  return (
    <div>
      <header>
        <p id="title">Bookish Melodies</p>
        <Router>
          <Navigation />
          <Routes>
            <Route path="/SearchBar" element={<SearchBar />} />
            <Route path="/topplaylists" element={<TopPlaylists />} />
          </Routes>


      </Router>
      <nav>
        <ul>
          <li><a href="./components/Search">Search</a></li>
          <li><a href="./components/TopPlaylists">TopPlaylists</a></li>
        </ul>
        </nav>
      </header>

      <main>
        <div className="title">
          <h1>Book-Inspired Playlist Generator</h1>
        </div>
        <div className="navigation">
          <Navigation />
          <BookSearcher />
          </div>
        <div className="login">
          <Login />
        </div>
     <SharedDataProvider>
      <SearchBar />
    </SharedDataProvider>
      </main>
      <footer>
        <p>&copy; 2023 Book-Inspired Playlist Generator</p>
      </footer>
    </div>
  );
}

export default App;
